mapper.update:

UPDATE  APPROVAL_NUM_INFO
SET     COMPLETE_YN = "C",
        CANCEL_DATE = NOW()
WHERE   TEL = #{tel}
AND     TYPE = #{type}
AND     COMPLETE_YN = #{completeYn}


UPDATE  APPROVAL_NUM_INFO
SET     COMPLETE_YN = "Y",
        COMPLETE_DATE = NOW()
WHERE   TEL = #{tel}
AND     TYPE = #{type}
AND     COMPLETE_YN = #{completeYn}
AND     AUTH_CODE = #{authCode}
